import { Grid, makeStyles, TextField } from '@material-ui/core';
import React, {useState} from 'react';
import reUseForm from '../Component/reUseForm';

const useStyles = makeStyles(theme =>({
    root:{
        '& .MuiFormControl-root' :{
            width:'80%',
            margin: theme.spacing(1)
        }

    }
}))


const initialFValues={
    id:0,
    fullName:'',
    email:'',
    mobile:'',
    city:'',
    gender:'male',
    class:'',
    admittedDate: new Date(),
}




export default function StudentForm (){
    const classes = useStyles();
    const {values,
        setValues, 
        handleInputChange}= reUseForm(initialFValues);
    return(

        <form className={classes.root}>
            <Grid container>
                <Grid item xs={6}>
                    <TextField value={values.fullName}  label="Full Name" 
                    variant="outlined" 
                    name='fullName'
                    onChange={handleInputChange}
                    />
                    <TextField value={values.email}  label="E-mail"
                    name="email"
                     variant="outlined" />

                </Grid>
                <Grid item xs={6}>


                </Grid>

            </Grid>


        </form>

    )

}
