import { FormControl, FormControlLabel, FormLabel,RadioGroup,Radio, Grid, TextField, } from '@material-ui/core';
import {UseForm,Form} from '../Component/reUseForm';
import Input  from  '../Component/controls/input';
import RadioGrp  from  '../Component/controls/RadioGroup';




const initialFValues={
    id:0,
    fullName:'',
    email:'',
    mobile:'',
    city:'',
    gender:'male',
    class:'',
    admittedDate: new Date(),
}


const genderItems=[
    {id:'male', title:'Male'},
    {id:'female', title:'Female'},
    {id:'others', title:'Bobbyrisky'},
]

export default function StudentForm (){

    const {values,
        setValues, 
         handleInputChange}= UseForm(initialFValues);

    return(
        <Form>
            <Grid container>
                <Grid item xs={6}>
               <Input

               name='fullName'
               label='Full Name'
               value={values.fullName}
               onChange={handleInputChange}
               />
            
            <Input
    
               name='email'
               label='E-mail'
               value={values.email}
               onChange={handleInputChange}
               />
            

                </Grid>
                <Grid item xs={6}>
                    <RadioGrp label='gender' name='gender' value={values.gender} onChange={handleInputChange}/>




            <FormControl>
                <FormLabel>
                    Gender
                </FormLabel>




                <RadioGroup row name='gender' value={values.gender} onChange={handleInputChange}>
                    <FormControlLabel value="male" control={<Radio/>} label='Male' />
                    <FormControlLabel value="female" control={<Radio/>} label='Female' />
                    <FormControlLabel value="others" control={<Radio/>} label='Bobyrisky' />
                </RadioGroup>
            </FormControl>
                </Grid>

            </Grid>

            </Form>
    

    )

}
